import Swal from 'sweetalert2';
import axios from 'axios';
import { Button, Form, Input } from 'semantic-ui-react';
import {Formik} from 'formik'
import * as yup from 'yup';

import styles from './PhoneUpdate.module.scss';



const validationSchema = yup.object({
  // phonenumber: yup
  // .number('Enter your phone number')
  // .min(10,'Enter a valid phone number')
  // .required('Phone number is required')

});

function PhoneUpdate() {
  const successAlert = () => {
    Swal.fire({
        title: 'successful',
        text: 'Your phone number has been updated.',
        icon: 'success'
      });
}


  return (
    <div className={styles.form}>
      <h3 className={styles.title}>Phone Number</h3>
      <Formik
        initialValues={{
          phone_number:''
        }}
        validationSchema={validationSchema}
        validateOnChange={false}
        validateOnBlur={true}
        onSubmit={(values, actions) =>{

          axios({
            method: 'post',
            url: 'http://3.129.45.198/api/auth/update-profile',
            headers: {
              "Authorization" : `Bearer ${localStorage.getItem('token')}`,
              'Accept' : 'application/json',
              'Content-Type': 'application/json'
            },
            data: {
              username: localStorage.getItem('username'),
              name: localStorage.getItem('name'),
              phone_number: values.phone_number,
              email: localStorage.getItem('phone_number'),
              subscription_fee: localStorage.getItem('subscription_fee'),
              location: localStorage.getItem('location'),
              website: localStorage.getItem('website'),
              profile: ''
            },
          })
            .then(function(response) {
              if(response.data.status) {
                localStorage.setItem('email', response.data.data[0].email);
                localStorage.setItem('name', response.data.data[0].name);
                localStorage.setItem('username', response.data.data[0].username);
                localStorage.setItem('website', response.data.data[0].website);
                localStorage.setItem('subscription_fee', response.data.data[0].subscription_fee);
                localStorage.setItem('location', response.data.data[0].location);
                localStorage.setItem('phone_number', response.data.data[0].phone_number);

                Swal.fire({
                  title: 'Success',
                  text: 'Phone Number Updated Successfully',
                  icon: 'success'
                }).then(() => {
                  actions.setSubmitting(false)
                });
              } else {
                Swal.fire({
                  title: 'Error',
                  text: response.data.message,
                  icon: 'warning'
                }).then(() => {
                  actions.setSubmitting(false)
                });
              }
          }).catch(res => {
            window.location.reload(false);
          });

        }}
        >
          {({touched, errors, handleChange, handleBlur,handleSubmit, isSubmitting})=> (


      <Form onSubmit={handleSubmit}>
        <Form.Field>
          <label className={styles.label}>Your Phone Number</label>
          <Input placeholder="Phone"
          onBlur={handleBlur}
          onChange={handleChange}
            defaultValue={localStorage.getItem('phone_number') == "null" ? "" : localStorage.getItem('phone_number')}
            required="required"
            name="phone_number"
            />
        </Form.Field>

        <div className={styles.button_submit}>
          <Button
          disabled={isSubmitting}
          loading={isSubmitting}
          primary  >Update</Button>
        </div>
      </Form>
      )}
      </Formik>
    </div>
  );
}

export default PhoneUpdate;
